buttons.script:
(function () {
    var panelWindow = (this instanceof Panel) ? this : new Window("palette", "Cache & Project Cleaner", undefined);
    panelWindow.orientation = "column";
    panelWindow.alignChildren = ["center", "top"];
    panelWindow.spacing = 10;
    panelWindow.margins = 16;

    var titleText = panelWindow.add("statictext", undefined, "AE Cache & Project Tools");
    titleText.alignment = ["center", "top"];
    try { titleText.graphics.font = ScriptUI.newFont("Arial", "BOLD", 14); } catch (_) {}

    var buttonGroup = panelWindow.add("group", undefined);
    buttonGroup.orientation = "column";
    buttonGroup.alignChildren = ["fill", "top"];
    buttonGroup.spacing = 8;
    buttonGroup.margins = 0;

    var allMemoryAndDiskCacheBtn = buttonGroup.add("button", undefined, "Purge All Memory & Disk Cache");
    var purgeRAMPreviewsBtn = buttonGroup.add("button", undefined, "Purge RAM Previews (Memory)");
    var reopenProjectBtn = buttonGroup.add("button", undefined, "Reopen Project (Clear Glitches)");

    var separator = panelWindow.add("panel", undefined, undefined);
    separator.alignment = ["fill", "top"];
    separator.height = 2;

    var statusText = panelWindow.add("statictext", undefined, "Ready");
    statusText.alignment = ["center", "top"];
    statusText.preferredSize.width = 280;

    function updateStatus(message, isError) {
        statusText.text = message;
        try {
            var g = statusText.graphics;
            var pen = g.newPen(g.PenType.SOLID_COLOR, isError ? [1, 0, 0] : [0, 0.6, 0], 1);
            g.foregroundColor = pen;
        } catch (_) {}
    }

    allMemoryAndDiskCacheBtn.onClick = function () {
        try {
            app.executeCommand(10200);
            updateStatus("All Memory & Disk Cache Purged!", false);
        } catch (err) {
            alert("Error (Btn1): " + err.toString());
            updateStatus("Error (Btn1): " + err.toString(), true);
        }
    };

    purgeRAMPreviewsBtn.onClick = function () {
        try {
            if (PurgeTarget && (PurgeTarget.ALL_MEMORY_CACHES !== undefined)) {
                app.purge(PurgeTarget.ALL_MEMORY_CACHES);
                updateStatus("RAM previews purged (memory cache).", false);
            } else {
                app.purge(PurgeTarget.IMAGE_CACHES);
                try { app.purge(PurgeTarget.SNAPSHOT_CACHES); } catch (_) {}
                updateStatus("RAM previews purged (image/snapshot caches).", false);
            }
        } catch (err) {
            alert("Error (Btn2): " + err.toString());
            updateStatus("Error (Btn2): " + err.toString(), true);
        }
    };

    reopenProjectBtn.onClick = function () {
        try {
            if (!app.project) {
                var msgNoProj = "Error (Btn3): No project is open.";
                alert(msgNoProj);
                updateStatus(msgNoProj, true);
                return;
            }

            var currentProject = app.project;
            var projectFile = currentProject.file;

            if (!projectFile) {
                var msgNeedSave = "Error (Btn3): Project must be saved once. Please save and try again.";
                alert(msgNeedSave);
                updateStatus(msgNeedSave, true);
                return;
            }

            var projectPath = projectFile.fsName;
            var projectName = projectFile.displayName;

            updateStatus("Saving project: " + projectName + "...", false);
            currentProject.save();

            updateStatus("Closing project: " + projectName + "...", false);
            app.beginSuppressDialogs();
            currentProject.close(CloseOptions.DO_NOT_SAVE_CHANGES);

            updateStatus("Reopening project: " + projectName + "...", false);
            var reopenedProject = app.open(new File(projectPath));

            if (reopenedProject) {
                updateStatus("Project reopened: " + projectName, false);
            } else {
                var msgFail = "Error (Btn3): Failed to reopen project " + projectName;
                alert(msgFail);
                updateStatus(msgFail, true);
            }

            app.endSuppressDialogs(false);
        } catch (err) {
            try { app.endSuppressDialogs(false); } catch (_) {}
            alert("Error (Btn3): " + err.toString());
            updateStatus("Error (Btn3): " + err.toString(), true);
        }
    };

    if (panelWindow instanceof Window) {
        panelWindow.center();
        panelWindow.show();
    } else {
        panelWindow.layout.layout(true);
        panelWindow.layout.resize();
    }
})();

(function() {
    if (app.project === null) {
        alert("Please open a project!");
        return;
    }

    if (app.project.activeItem === null || !(app.project.activeItem instanceof CompItem)) {
        alert("Please select a composition!");
        return;
    }

    if (app.project.activeItem.selectedLayers.length === 0) {
        alert("Please select a layer!");
        return;
    }

    var comp = app.project.activeItem;
    var layer = comp.selectedLayers[0];

    var info = "";

    info += "===== BASIC LAYER INFO =====\n";
    info += "Layer Name: " + layer.name + "\n";
    info += "Layer Index: " + layer.index + "\n";
    info += "Layer ID: " + layer.id + "\n";
    info += "Layer Type: " + getLayerType(layer) + "\n";

    info += "\n===== TIME INFO =====\n";
    info += "In Point: " + timeToFrames(layer.inPoint, comp.frameRate) + " (" + layer.inPoint + " sec)\n";
    info += "Out Point: " + timeToFrames(layer.outPoint, comp.frameRate) + " (" + layer.outPoint + " sec)\n";
    info += "Duration: " + (layer.outPoint - layer.inPoint) + " sec\n";
    info += "Start Time: " + layer.startTime + " sec\n";

    info += "\n===== TRANSFORMS =====\n";
    info += "Position: " + getPropertyValues(layer.transform.position) + "\n";
    info += "Scale: " + getPropertyValues(layer.transform.scale) + "%\n";
    info += "Rotation: " + getPropertyValues(layer.transform.rotation) + " degrees\n";
    info += "Opacity: " + layer.transform.opacity.value + "%\n";

    if (layer.transform.anchorPoint !== undefined) {
        info += "Anchor Point: " + getPropertyValues(layer.transform.anchorPoint) + "\n";
    }

    if (layer.mask && layer.mask.numProperties > 0) {
        info += "\n===== MASKS =====\n";
        for (var i = 1; i <= layer.mask.numProperties; i++) {
            var mask = layer.mask(i);
            info += "Mask " + i + ": " + mask.name + ", Mode: " + getMaskMode(mask.maskMode) + "\n";
        }
    }

    if (layer.effect && layer.effect.numProperties > 0) {
        info += "\n===== EFFECTS =====\n";
        for (var i = 1; i <= layer.effect.numProperties; i++) {
            var effect = layer.effect(i);
            info += "Effect " + i + ": " + effect.name + "\n";
        }
    }

    if (layer instanceof TextLayer || layer instanceof ShapeLayer) {
        if (layer.layerStyle && layer.layerStyle.numProperties > 0) {
            info += "\n===== LAYER STYLES =====\n";
            for (var i = 1; i <= layer.layerStyle.numProperties; i++) {
                var style = layer.layerStyle(i);
                info += "Style " + i + ": " + style.name + "\n";
            }
        }
    }

    if (layer instanceof TextLayer) {
        info += "\n===== TEXT PROPERTIES =====\n";
        try {
            info += "Content: " + layer.text.sourceText.value + "\n";
        } catch (e) {
            info += "Content: [Could not retrieve]\n";
        }

        try {
            var textDoc = layer.text.sourceText.value;
            info += "Font: " + textDoc.font + "\n";
            info += "Font Size: " + textDoc.fontSize + "\n";
            info += "Text Color: " + rgbToHex(textDoc.fillColor) + "\n";
        } catch (e) {
            info += "Could not retrieve additional text properties\n";
        }
    }

    if (layer instanceof AVLayer) {
        info += "\n===== MEDIA PROPERTIES =====\n";
        info += "Source Name: " + (layer.source ? layer.source.name : "No source") + "\n";

        if (layer.source) {
            if (layer.source instanceof FootageItem) {
                info += "Source Type: Footage\n";
                info += "Size: " + layer.source.width + "x" + layer.source.height + "\n";
                info += "Duration: " + layer.source.duration + " sec\n";
                info += "Frame Rate: " + (layer.source.frameRate || "N/A") + " fps\n";
            } else if (layer.source instanceof CompItem) {
                info += "Source Type: Composition\n";
                info += "Size: " + layer.source.width + "x" + layer.source.height + "\n";
                info += "Duration: " + layer.source.duration + " sec\n";
                info += "Frame Rate: " + layer.source.frameRate + " fps\n";
            }
        }

        info += "Quality: " + getQuality(layer.quality) + "\n";
        info += "Audio Enabled: " + (layer.audioEnabled ? "Yes" : "No") + "\n";
        info += "Visible: " + (layer.enabled ? "Yes" : "No") + "\n";
        info += "3D Layer: " + (layer.threeDLayer ? "Yes" : "No") + "\n";
        info += "Blending Mode: " + getBlendingMode(layer.blendingMode) + "\n";
    }

    info += "\n===== SWITCHES =====\n";
    info += "Visible: " + (layer.enabled ? "Yes" : "No") + "\n";
    info += "Locked: " + (layer.locked ? "Yes" : "No") + "\n";
    info += "Shy: " + (layer.shy ? "Yes" : "No") + "\n";
    info += "Continuously Rasterize: " + (layer.continuesRasterization ? "Yes" : "No") + "\n";
    info += "Motion Blur: " + getMotionBlurType(layer.motionBlur, layer.adjustmentLayer) + "\n";

    if (layer.trackMatteType !== TrackMatteType.NO_TRACK_MATTE) {
        info += "Track Matte: " + getTrackMatteType(layer.trackMatteType) + "\n";
    }

    var dialog = new Window("dialog", "Layer Info: " + layer.name);
    dialog.orientation = "column";
    dialog.alignChildren = ["fill", "top"];
    dialog.spacing = 10;
    dialog.margins = 16;

    var infoGroup = dialog.add("group");
    infoGroup.orientation = "column";
    infoGroup.alignChildren = ["fill", "top"];

    var infoText = infoGroup.add("edittext", undefined, info, {multiline: true, scrollable: true});
    infoText.preferredSize.width = 600;
    infoText.preferredSize.height = 500;

    var btnGroup = dialog.add("group");
    btnGroup.orientation = "row";
    btnGroup.alignChildren = ["center", "center"];

    var okBtn = btnGroup.add("button", undefined, "OK");
    var copyBtn = btnGroup.add("button", undefined, "Copy");

    copyBtn.onClick = function() {
        infoText.active = true;
        infoText.selectAll();
        app.executeCommand(app.findMenuCommandId("Copy"));
        infoText.notify("onDeselect");
        alert("Information copied to clipboard!");
    };

    okBtn.onClick = function() {
        dialog.close();
    };

    dialog.show();

    function getLayerType(layer) {
        if (layer instanceof TextLayer) return "Text Layer";
        if (layer instanceof ShapeLayer) return "Shape Layer";
        if (layer instanceof LightLayer) return "Light Layer";
        if (layer instanceof CameraLayer) return "Camera Layer";
        if (layer instanceof AVLayer) {
            if (layer.source instanceof CompItem) return "Pre-composition";
            if (layer.adjustmentLayer) return "Adjustment Layer";
            if (layer.nullLayer) return "Null Object";
            if (layer.source instanceof SolidSource) return "Solid";
            return "AV Layer";
        }
        return "Unknown Type";
    }

    function getPropertyValues(prop) {
        try {
            if (prop.value instanceof Array) {
                if (prop.value.length === 2) {
                    return "[" + Math.round(prop.value[0] * 1000) / 1000 + ", " + Math.round(prop.value[1] * 1000) / 1000 + "]";
                } else if (prop.value.length === 3) {
                    return "[" + Math.round(prop.value[0] * 1000) / 1000 + ", " + Math.round(prop.value[1] * 1000) / 1000 + ", " + Math.round(prop.value[2] * 1000) / 1000 + "]";
                }
                return prop.value.toString();
            } else {
                return Math.round(prop.value * 1000) / 1000;
            }
        } catch (e) {
            return "Could not retrieve value";
        }
    }

    function getMaskMode(mode) {
        switch (mode) {
            case MaskMode.NONE: return "None";
            case MaskMode.ADD: return "Add";
            case MaskMode.SUBTRACT: return "Subtract";
            case MaskMode.INTERSECT: return "Intersect";
            case MaskMode.LIGHTEN: return "Lighten";
            case MaskMode.DARKEN: return "Darken";
            case MaskMode.DIFFERENCE: return "Difference";
            default: return "Unknown";
        }
    }

    function getQuality(quality) {
        switch (quality) {
            case LayerQuality.WIREFRAME: return "Wireframe";
            case LayerQuality.DRAFT: return "Draft";
            case LayerQuality.BEST: return "Best";
            default: return "Unknown";
        }
    }

    function getBlendingMode(mode) {
        switch (mode) {
            case BlendingMode.NORMAL: return "Normal";
            case BlendingMode.MULTIPLY: return "Multiply";
            case BlendingMode.SCREEN: return "Screen";
            case BlendingMode.OVERLAY: return "Overlay";
            case BlendingMode.SOFT_LIGHT: return "Soft Light";
            case BlendingMode.HARD_LIGHT: return "Hard Light";
            case BlendingMode.COLOR_DODGE: return "Color Dodge";
            case BlendingMode.COLOR_BURN: return "Color Burn";
            case BlendingMode.DARKEN: return "Darken";
            case BlendingMode.LIGHTEN: return "Lighten";
            case BlendingMode.DIFFERENCE: return "Difference";
            case BlendingMode.EXCLUSION: return "Exclusion";
            case BlendingMode.HUE: return "Hue";
            case BlendingMode.SATURATION: return "Saturation";
            case BlendingMode.COLOR: return "Color";
            case BlendingMode.LUMINOSITY: return "Luminosity";
            case BlendingMode.ADD: return "Add";
            case BlendingMode.ALPHA_ADD: return "Alpha Add";
            case BlendingMode.CLASSIC_COLOR_BURN: return "Classic Color Burn";
            case BlendingMode.CLASSIC_COLOR_DODGE: return "Classic Color Dodge";
            case BlendingMode.CLASSIC_DIFFERENCE: return "Classic Difference";
            default: return "Unknown";
        }
    }

    function getTrackMatteType(type) {
        switch (type) {
            case TrackMatteType.ALPHA: return "Alpha Matte";
            case TrackMatteType.ALPHA_INVERTED: return "Alpha Inverted Matte";
            case TrackMatteType.LUMA: return "Luma Matte";
            case TrackMatteType.LUMA_INVERTED: return "Luma Inverted Matte";
            default: return "No Matte";
        }
    }

    function getMotionBlurType(motionBlur, isAdjustmentLayer) {
        if (isAdjustmentLayer) {
            return "Not applicable (adjustment layer)";
        } else {
            return motionBlur ? "Enabled" : "Disabled";
        }
    }

    function timeToFrames(time, frameRate) {
        return Math.round(time * frameRate) + " frames";
    }

    function rgbToHex(rgbColor) {
        try {
            var r = Math.round(rgbColor[0] * 255);
            var g = Math.round(rgbColor[1] * 255);
            var b = Math.round(rgbColor[2] * 255);
            return "#" + r.toString(16).padStart(2, "0") + 
                   g.toString(16).padStart(2, "0") + 
                   b.toString(16).padStart(2, "0");
        } catch (e) {
            return "Could not get color";
        }
    }
})();

(function createProjectFolders() {
    app.beginUndoGroup("Create Project Folders");

    var folderNames = ["Comp", "Image", "Video", "Sound"];

    for (var i = 0; i < folderNames.length; i++) {
        var folderName = folderNames[i];
        var folderExists = false;

        for (var j = 1; j <= app.project.numItems; j++) {
            if ((app.project.item(j) instanceof FolderItem) && (app.project.item(j).name === folderName)) {
                folderExists = true;
                break;
            }
        }

        if (!folderExists) {
            app.project.items.addFolder(folderName);
        }
    }

    app.endUndoGroup();

})();

(function organizeRootItemsStable() {

    function findFolder(name) {
        for (var i = 1; i <= app.project.numItems; i++) {
            if ((app.project.item(i) instanceof FolderItem) && (app.project.item(i).name === name)) {
                return app.project.item(i);
            }
        }
        return null;
    }

    app.beginUndoGroup("Stable Sort Files from Root");

    var soundFolder = findFolder("Sound");
    var videoFolder = findFolder("Video");
    var imageFolder = findFolder("Image");

    if (!soundFolder || !videoFolder || !imageFolder) {
        var missingFolders = [];
        if (!soundFolder) missingFolders.push("Sound");
        if (!videoFolder) missingFolders.push("Video");
        if (!imageFolder) missingFolders.push("Image");
        alert("Error: Could not find folders for sorting.\nPlease create folder(s): " + missingFolders.join(", ") + ".");
        app.endUndoGroup();
        return;
    }

    var itemsToMove = []; 

    for (var i = 1; i <= app.project.numItems; i++) {
        var currentItem = app.project.item(i);

        if (currentItem.parentFolder === app.project.rootFolder && currentItem instanceof FootageItem) {
            if (!(currentItem.mainSource instanceof SolidSource)) {
                itemsToMove.push(currentItem);
            }
        }
    }

    var movedAudio = 0, movedVideo = 0, movedImage = 0;

    for (var j = 0; j < itemsToMove.length; j++) {
        var item = itemsToMove[j];

        if (item.mainSource.isStill) {
            item.parentFolder = imageFolder;
            movedImage++;
        } else if (item.hasVideo) {
            item.parentFolder = videoFolder;
            movedVideo++;
        } else if (item.hasAudio) {
            item.parentFolder = soundFolder;
            movedAudio++;
        }
    }

    app.endUndoGroup();

    if (movedAudio === 0 && movedVideo === 0 && movedImage === 0) {
        alert("Sorting completed.\n\nNo files found for moving in project root.");
    } else {
        var report = "Sorting successfully completed!\n\n";
        report += "Moved video: " + movedVideo + "\n";
        report += "Moved images: " + movedImage + "\n";
        report += "Moved audio: " + movedAudio + "\n";
        alert(report);
    }

})();

{
    if (app.project && app.project.activeItem instanceof CompItem) {
        var comp = app.project.activeItem;
        var selectedLayers = comp.selectedLayers;

        if (selectedLayers.length > 0) {
            app.beginUndoGroup("Gaussian Blur Animation");

            for (var i = 0; i < selectedLayers.length; i++) {
                var layer = selectedLayers[i];

                if (layer) {
                    var blurEffect = layer.Effects.addProperty("Gaussian Blur");

                    var blurProp = blurEffect.property("Blurriness");

                    var startTime = layer.inPoint;
                    var endTime = startTime + 1.0;

                    blurProp.setValueAtTime(startTime, 0);

                    blurProp.setValueAtTime(endTime, 80);

                    var key1Index = blurProp.nearestKeyIndex(startTime);
                    var key2Index = blurProp.nearestKeyIndex(endTime);

                    var easeIn = new KeyframeEase(0, 33.33);
                    var easeOut = new KeyframeEase(0, 33.33);

                    blurProp.setTemporalEaseAtKey(key1Index, [easeOut], [easeOut]);

                    blurProp.setTemporalEaseAtKey(key2Index, [easeIn], [easeIn]);
                }
            }

            app.endUndoGroup();
        }
    }
}

{
    if (app.project && app.project.activeItem instanceof CompItem) {
        var comp = app.project.activeItem;
        var selectedLayers = comp.selectedLayers;

        if (selectedLayers.length > 0) {
            app.beginUndoGroup("Scale Layer to Comp Size");

            for (var i = 0; i < selectedLayers.length; i++) {
                var layer = selectedLayers[i];

                if (layer.transform) {
                    var compWidth = comp.width;
                    var compHeight = comp.height;

                    var bounds = layer.sourceRectAtTime(comp.time, false);

                    if (bounds && bounds.width > 0 && bounds.height > 0) {
                        var anchorX = bounds.left + bounds.width / 2;
                        var anchorY = bounds.top + bounds.height / 2;
                        layer.transform.anchorPoint.setValue([anchorX, anchorY]);

                        var scaleX = (compWidth / bounds.width) * 100;
                        var scaleY = (compHeight / bounds.height) * 100;

                        layer.transform.scale.setValue([scaleX, scaleY]);

                        layer.transform.position.setValue([compWidth / 2, compHeight / 2]);
                    }
                }
            }

            app.endUndoGroup();
        }
    }
}

