{
    if (app.project && app.project.activeItem instanceof CompItem) {
        var comp = app.project.activeItem;
        var selectedLayers = comp.selectedLayers;

        if (selectedLayers.length > 0) {
            app.beginUndoGroup("Fade In Animation");

            for (var i = 0; i < selectedLayers.length; i++) {
                var layer = selectedLayers[i];

                if (layer.transform && layer.transform.opacity) {
                    var startTime = layer.inPoint;
                    var endTime = startTime + 0.5;

                    var opacityProp = layer.transform.opacity;

                    opacityProp.setValueAtTime(startTime, 0);

                    opacityProp.setValueAtTime(endTime, 100);

                    var key1Index = opacityProp.nearestKeyIndex(startTime);
                    var key2Index = opacityProp.nearestKeyIndex(endTime);

                    var easeIn = new KeyframeEase(0, 33.33);
                    var easeOut = new KeyframeEase(0, 33.33);

                    opacityProp.setTemporalEaseAtKey(key1Index, [easeOut], [easeOut]);

                    opacityProp.setTemporalEaseAtKey(key2Index, [easeIn], [easeIn]);
                }
            }

            app.endUndoGroup();
        }
    }
}

{
    if (app.project && app.project.activeItem instanceof CompItem) {
        var comp = app.project.activeItem;
        var selectedLayers = comp.selectedLayers;

        if (selectedLayers.length > 0) {
            app.beginUndoGroup("Fade Out Animation");

            for (var i = 0; i < selectedLayers.length; i++) {
                var layer = selectedLayers[i];

                if (layer.transform && layer.transform.opacity) {
                    var endTime = layer.outPoint;
                    var startTime = endTime - 0.5;

                    var opacityProp = layer.transform.opacity;

                    opacityProp.setValueAtTime(startTime, 100);

                    opacityProp.setValueAtTime(endTime, 0);

                    var key1Index = opacityProp.nearestKeyIndex(startTime);
                    var key2Index = opacityProp.nearestKeyIndex(endTime);

                    var easeIn = new KeyframeEase(0, 33.33);
                    var easeOut = new KeyframeEase(0, 33.33);

                    opacityProp.setTemporalEaseAtKey(key1Index, [easeOut], [easeOut]);

                    opacityProp.setTemporalEaseAtKey(key2Index, [easeIn], [easeIn]);
                }
            }

            app.endUndoGroup();
        }
    }
}

(function() {

    if (!(app.project.activeItem instanceof CompItem)) {
        alert("Please open a composition!");
        return;
    }

    var activeComp = app.project.activeItem;
    var selectedLayers = activeComp.selectedLayers;

    if (selectedLayers.length === 0) {
        alert("Please select a precomposition layer on the timeline!");
        return;
    }

    var selectedLayer = selectedLayers[0];
    if (!selectedLayer.source || !(selectedLayer.source instanceof CompItem)) {
        alert("Selected layer is not a precomposition!");
        return;
    }

    var win = new Window("dialog", "True Comp Duplicator");
    win.orientation = "column";
    win.alignChildren = "center";
    win.spacing = 12;
    win.margins = 15;
    win.preferredSize.width = 320;

    var headerGroup = win.add("group");
    headerGroup.alignment = "fill";
    headerGroup.spacing = 8;

    var sourceGroup = headerGroup.add("group");
    sourceGroup.alignment = "left";
    sourceGroup.alignChildren = "left";
