    function duplicateCompRecursive(comp, nameText, isPrefix, copyIndex, map) {
        if (map[comp.id]) {
            return map[comp.id];
        }

        var newName;
        if (isPrefix) {
            newName = nameText + comp.name;
        } else {
            newName = comp.name + nameText;
        }

        if (copyIndex > 0) {
            newName += "_" + (copyIndex + 1);
        }

        var newComp = app.project.items.addComp(
            newName,
            comp.width,
            comp.height,
            comp.pixelAspect,
            comp.duration,
            comp.frameRate
        );

        newComp.bgColor = comp.bgColor;
        newComp.workAreaStart = comp.workAreaStart;
        newComp.workAreaDuration = comp.workAreaDuration;

        try {
            if (comp.displayStartTime !== undefined) {
                newComp.displayStartTime = comp.displayStartTime;
            }
        } catch (e) {
        }

        map[comp.id] = newComp;

        for (var i = comp.numLayers; i >= 1; i--) {
            var originalLayer = comp.layer(i);
            var newLayer;

            try {
                if (originalLayer.source instanceof CompItem) {
                    var duplicatedSource = duplicateCompRecursive(originalLayer.source, nameText, isPrefix, copyIndex, map);
                    newLayer = newComp.layers.add(duplicatedSource);
                } else if (originalLayer.source) {
                    newLayer = newComp.layers.add(originalLayer.source);
                } else if (originalLayer.matchName === "ADBE Text Layer") {
                    newLayer = newComp.layers.addText();
                    if (originalLayer.property("ADBE Text Properties").property("ADBE Text Document")) {
                        var textDoc = originalLayer.property("ADBE Text Properties").property("ADBE Text Document").value;
                        newLayer.property("ADBE Text Properties").property("ADBE Text Document").setValue(textDoc);
                    }
                } else if (originalLayer.matchName === "ADBE Vector Layer") {
                    newLayer = newComp.layers.addShape();
                } else if (originalLayer.matchName === "ADBE AV Layer") {
                    newLayer = newComp.layers.addSolid([1, 1, 1], originalLayer.name, comp.width, comp.height, comp.pixelAspect, comp.duration);
                    newLayer.adjustmentLayer = true;
                } else {
                    continue;
                }

                newLayer.name = originalLayer.name;
                newLayer.startTime = originalLayer.startTime;
                newLayer.inPoint = originalLayer.inPoint;
                newLayer.outPoint = originalLayer.outPoint;
                newLayer.enabled = originalLayer.enabled;
                newLayer.solo = originalLayer.solo;
                newLayer.locked = originalLayer.locked;
                newLayer.shy = originalLayer.shy;
                newLayer.blendingMode = originalLayer.blendingMode;

                try {
                    if (originalLayer.layerStyles) {
                    }
                } catch (e) {
                }

                copyTransform(originalLayer, newLayer);
                copyEffects(originalLayer, newLayer);

            } catch (e) {
                continue;
            }
        }

        return newComp;
    }

    function copyTransform(fromLayer, toLayer) {
        try {
            var fromTransform = fromLayer.transform;
            var toTransform = toLayer.transform;

            copyProperty(fromTransform.position, toTransform.position);
            copyProperty(fromTransform.anchorPoint, toTransform.anchorPoint);
            copyProperty(fromTransform.scale, toTransform.scale);
            copyProperty(fromTransform.rotation, toTransform.rotation);
            copyProperty(fromTransform.opacity, toTransform.opacity);

            try {
                if (fromLayer.threeDLayer) {
                    toLayer.threeDLayer = true;
                    copyProperty(fromTransform.xRotation, toTransform.xRotation);
                    copyProperty(fromTransform.yRotation, toTransform.yRotation);
                    copyProperty(fromTransform.orientation, toTransform.orientation);
                }
            } catch (e) {
            }

        } catch (e) {
        }
    }

    function copyEffects(fromLayer, toLayer) {
        try {
            var fromEffects = fromLayer.property("ADBE Effect Parade");
            var toEffects = toLayer.property("ADBE Effect Parade");

            if (fromEffects && fromEffects.numProperties > 0) {
                for (var e = 1; e <= fromEffects.numProperties; e++) {
                    var effect = fromEffects.property(e);
                    var newEffect = toEffects.addProperty(effect.matchName);

                    for (var p = 1; p <= effect.numProperties; p++) {
                        try {
                            var prop = effect.property(p);
                            var newProp = newEffect.property(p);
                            copyProperty(prop, newProp);
                        } catch (e) {
                        }
                    }
                }
            }
        } catch (e) {
        }
    }

    function copyProperty(fromProp, toProp) {
        try {
            if (!fromProp || !toProp) return;

            if (fromProp.numKeys > 0) {
                for (var k = 1; k <= fromProp.numKeys; k++) {
                    var keyTime = fromProp.keyTime(k);
                    var keyValue = fromProp.keyValue(k);

                    toProp.setValueAtTime(keyTime, keyValue);

                    try {
                        var inInterp = fromProp.keyInInterpolationType(k);
                        var outInterp = fromProp.keyOutInterpolationType(k);
                        toProp.setInterpolationTypeAtKey(k, inInterp, outInterp);

                        if (inInterp === KeyframeInterpolationType.BEZIER || outInterp === KeyframeInterpolationType.BEZIER) {
                            var inTangent = fromProp.keyInTemporalEase(k);
                            var outTangent = fromProp.keyOutTemporalEase(k);
                            toProp.setTemporalEaseAtKey(k, inTangent, outTangent);
                        }
                    } catch (e) {
                    }
                }
            } else {
                toProp.setValue(fromProp.value);
            }
        } catch (e) {
        }
    }

})();

