(function createAdvancedParentLink() {

    app.beginUndoGroup("Advanced Dynamic Parent Link");

    var activeComp = app.project.activeItem;
    if (!activeComp || !(activeComp instanceof CompItem)) {
        alert("Please open a composition.");
        return;
    }

    var selectedLayer = activeComp.selectedLayers[0];
    if (!selectedLayer) {
        alert("Please select a layer to link.");
        return;
    }

    var dialog = new Window("dialog", "Parent Link with Preserved Position");
    dialog.orientation = "column";
    dialog.alignChildren = ["fill", "top"];
    dialog.add("statictext", undefined, "Select parent layer:");

    var layerDropdown = dialog.add("dropdownlist", undefined, []);
    for (var i = 1; i <= activeComp.numLayers; i++) {
        if (activeComp.layer(i).index !== selectedLayer.index) {
            layerDropdown.add("item", activeComp.layer(i).name);
        }
    }
    layerDropdown.selection = 0;

    if (layerDropdown.items.length === 0) {
        alert("No other layers available for parenting.");
        return;
    }

    var buttonGroup = dialog.add("group");
    buttonGroup.orientation = "row";
    buttonGroup.alignment = ["right", "top"];
    var okButton = buttonGroup.add("button", undefined, "Link", { name: "ok" });
    buttonGroup.add("button", undefined, "Cancel", { name: "cancel" });

    okButton.onClick = function() {
        if (!layerDropdown.selection) return;

        var targetLayer = activeComp.layer(layerDropdown.selection.text);
        if (!targetLayer) return;

        var timeNow = activeComp.time;

        var posOffset = selectedLayer.transform.position.valueAtTime(timeNow, false) - targetLayer.transform.position.valueAtTime(timeNow, false);
        var scaleOffset = selectedLayer.transform.scale.valueAtTime(timeNow, false) - targetLayer.transform.scale.valueAtTime(timeNow, false);
        var rotOffset = 0;

        if (selectedLayer.transform.property("Rotation")) {
            rotOffset = selectedLayer.transform.rotation.valueAtTime(timeNow, false) - targetLayer.transform.rotation.valueAtTime(timeNow, false);
        }

        var checkboxEffect = selectedLayer.property("Effects").addProperty("ADBE Checkbox Control");
        checkboxEffect.name = "Enable Link";
        checkboxEffect.property("Checkbox").setValue(true);
        var safeCheckboxName = JSON.stringify(checkboxEffect.name);

        var safeTargetName = JSON.stringify(targetLayer.name);

        var expressionTemplate = 
        'try {\n' +
        '    var target = thisComp.layer(%TARGET_NAME%);\n' +
        '    var checkbox = effect(%CHECKBOX_NAME%)("Checkbox");\n' +
        '    \n' +
        '    if (checkbox.valueAtTime(time) == 1) {\n' +
        '        target.transform.position + [%POS_OFFSET%];\n' +
        '    } else {\n' +
        '        var t = time - thisComp.frameDuration;\n' +
        '        var lastOnTime = -1;\n' +
        '        while (t >= thisLayer.inPoint) {\n' +
        '            if (checkbox.valueAtTime(t) == 1) {\n' +
        '                lastOnTime = t;\n' +
        '                break;\n' +
        '            }\n' +
        '            t -= thisComp.frameDuration;\n' +
        '        }\n' +
        '        \n' +
        '        if (lastOnTime != -1) {\n' +
        '            target.transform.position.valueAtTime(lastOnTime, false) + [%POS_OFFSET%];\n' +
        '        } else {\n' +
        '            value;\n' +
        '        }\n' +
        '    }\n' +
        '} catch (e) {\n' +
        '    value;\n' +
        '}';

        var posExpression = expressionTemplate.replace(/%TARGET_NAME%/g, safeTargetName)
                                            .replace(/%CHECKBOX_NAME%/g, safeCheckboxName)
                                            .replace(/%POS_OFFSET%/g, posOffset.join(','));
        selectedLayer.transform.position.expression = posExpression;

        var scaleExpression = expressionTemplate.replace(/%TARGET_NAME%/g, safeTargetName)
                                                .replace(/%CHECKBOX_NAME%/g, safeCheckboxName)
                                                .replace(/%POS_OFFSET%/g, scaleOffset.join(','))
                                                .replace(/position/g, 'scale');
        selectedLayer.transform.scale.expression = scaleExpression;

        if (selectedLayer.transform.property("Rotation")) {
            var rotExpression = expressionTemplate.replace(/%TARGET_NAME%/g, safeTargetName)
                                                  .replace(/%CHECKBOX_NAME%/g, safeCheckboxName)
                                                  .replace(/\[%POS_OFFSET%\]/g, rotOffset)
                                                  .replace(/position/g, 'rotation');
            selectedLayer.transform.rotation.expression = rotExpression;
        }

        dialog.close();
    };

    dialog.center();
    dialog.show();

    app.endUndoGroup();

})();

(function removeAllCheckboxControls() {

    var activeComp = app.project.activeItem;
    if (!activeComp || !(activeComp instanceof CompItem)) {
        alert("Please open a composition.");
        return;
    }

    var selectedLayers = activeComp.selectedLayers;
    if (selectedLayers.length === 0) {
        alert("Please select layer(s) to clean.");
        return;
    }

    app.beginUndoGroup("Remove All Checkbox Controls");

    var cleanedLayersCount = 0;

    for (var i = 0; i < selectedLayers.length; i++) {
        var currentLayer = selectedLayers[i];
        var wasCleaned = false;

        currentLayer.transform.position.expression = "";
        currentLayer.transform.scale.expression = "";
        if (currentLayer.transform.property("Rotation")) {
            currentLayer.transform.rotation.expression = "";
        }

        var effects = currentLayer.property("Effects");
        for (var j = effects.numProperties; j >= 1; j--) {
            var currentEffect = effects.property(j);

            if (currentEffect.matchName === "ADBE Checkbox Control") {
                currentEffect.remove();
                wasCleaned = true;
            }
        }
        if (wasCleaned) {
            cleanedLayersCount++;
        }
    }

    app.endUndoGroup();

})();

