        valuesBtnGroup.alignment = ["right", "center"];
        valuesBtnGroup.spacing = 10;

        var createBtn = valuesBtnGroup.add("button", undefined, "Create");
        var closeBtn = valuesBtnGroup.add("button", undefined, "Close");

        createBtn.onClick = function() {
            userChoices.blurValue = parseFloat(blurInput.text.toString().replace(',', '.')) || 100;
            userChoices.exposureValue = parseFloat(exposureInput.text.toString().replace(',', '.')) || -2;
            userChoices.shouldCreate = true;
            valuesDialog.close();
            durationDialog.close();
        };

        closeBtn.onClick = function() {
            valuesDialog.close();
            durationDialog.close();
        };

        valuesDialog.show();
    }

    durationDialog.show();

    if (!userChoices.shouldCreate) return;

    var comp = app.project.activeItem;
    if (!comp || !(comp instanceof CompItem)) {
        alert("Please open a composition first!");
        return;
    }

    var startTime = comp.time;
    var endTime = startTime + userChoices.duration;

    var solid = comp.layers.addSolid(
        [1, 0, 0],
        "Lumetri+Blur Transition", 
        comp.width, 
        comp.height, 
        1.0,
        comp.duration
    );
    solid.startTime = startTime;
    solid.adjustmentLayer = true;

    var effects = solid.property("ADBE Effect Parade");

    var blur = effects.addProperty("ADBE Gaussian Blur 2");
    var blurParam = blur.property("ADBE Gaussian Blur 2-0001");

    blurParam.setValueAtTime(startTime, 0);
    blurParam.setValueAtTime(endTime, userChoices.blurValue);

    applyEasyEase(blurParam);

    var lumetri = effects.addProperty("ADBE Lumetri");
    var exposureParam = lumetri.property("ADBE Lumetri-0011");

    exposureParam.setValueAtTime(startTime, 0);
    exposureParam.setValueAtTime(endTime, userChoices.exposureValue);

    applyEasyEase(exposureParam);

    comp.time = startTime;
    solid.selected = true;


    function applyEasyEase(prop) {
        if (prop.numKeys >= 2) {
            for (var i = 1; i <= prop.numKeys; i++) {
                prop.setInterpolationTypeAtKey(i, 
                    KeyframeInterpolationType.BEZIER, 
                    KeyframeInterpolationType.BEZIER);
                prop.setTemporalEaseAtKey(i, 
                    [new KeyframeEase(0, 33)], 
                    [new KeyframeEase(0, 33)]);
            }
        }
    }
})();

(function() {
    try {
        if (!app.project || !app.project.activeItem || !(app.project.activeItem instanceof CompItem)) {
            alert("Please open a composition", "Error");
            return;
        }
        var comp = app.project.activeItem;

        if (!comp.selectedLayers || comp.selectedLayers.length === 0) {
            alert("Please select a layer with audio", "Error");
            return;
        }
        var layer = comp.selectedLayers[0];

        var hasAudio = false;
        try {
            hasAudio = layer.hasAudio;
            if (!hasAudio) {
                var audioProps = layer.property("ADBE Audio Properties");
                hasAudio = (audioProps !== null);
            }
        } catch(e) {
            alert("Could not check layer audio properties", "Error");
            return;
        }

        if (!hasAudio) {
            alert("Selected layer does not contain audio", "Error");
            return;
        }

        var audioLevels = null;
        try {
            audioLevels = layer.property("ADBE Audio Properties").property("ADBE Audio Levels");
        } catch(e) {
            try {
                audioLevels = layer.property("Audio Levels");
            } catch(e) {
                alert("Could not find audio levels property", "Error");
                return;
            }
        }

        try {
            var actualStartTime = layer.inPoint;

            if (audioLevels.numKeys > 0) {
                if (!confirm("Layer already has animation keyframes. Replace them?")) {
                    return;
                }
                while (audioLevels.numKeys > 0) {
                    audioLevels.removeKey(1);
                }
            }

            audioLevels.setValueAtTime(actualStartTime, [-25, -25]);
            audioLevels.setValueAtTime(actualStartTime + 1, [0, 0]);

            alert("Audio fade animation successfully added!\nInitial value: -25 dB\nAfter 1 second: 0 dB", "Done");

        } catch(e) {
            alert("Error setting keyframes:\n" + e.toString(), "Error");
        }

    } catch(err) {
        alert("Critical error:\n" + err.toString() + "\n\nLine: " + err.line, "Script Error");
    }
})();
